set-header.value:
@{
            var exectime = context.Variables.GetValueOrDefault("endtime") - context.Variables.GetValueOrDefault("starttime");
            var exectimeinms = (exectime.Seconds * 1000 +  exectime.Milliseconds).ToString();
            return "policy1exectime: " + exectimeinms.ToString() + "ms";
            }

